query getZonesListing($roomType: String!, $categoryId: String!, $subCategoryId: String!, $zoneType: String, $designerEmail: String, $filterFields: [FieldRequest]) {
  getZoneListForType(zoneCategory: $categoryId, zoneSubCategory: $subCategoryId, roomType: $roomType, zoneType: $zoneType, designerEmail: $designerEmail, filterFields: $filterFields) {
    zoneId
    zoneName
    zonePrice
    zoneImage
    designedBy
    favourite
    zoneWidth
    zoneHeight
    zoneLength
    hlZoneId
    fieldData {
      fieldName
      fieldValue
      __typename
    }
    zoneTagsData {
      fieldName
      fieldValue
      fieldObjectValue {
        key
        value
        __typename
      }
      __typename
    }
    __typename
  }
}
