query getFurniture($roomType: String!, $productType: String!, $catalogProfileType: CatalogProfileType, $city: String, $cancelledSuborder: Boolean) {
  getFurniture(roomType: $roomType, productType: $productType, catalogProfileType: $catalogProfileType, city: $city, cancelledSuborder: $cancelledSuborder) {
    categoryId
    categoryName
    baseImage
    defaultName
    defaultImage
    subCategoryList {
      subCategoryId
      subCategoryName
      baseImage
      fieldProductCount {
        fieldName
        fieldValues
        isFilter
        __typename
      }
      __typename
    }
    __typename
  }
}
