query getColourData($materialId: String!, $materialType: String!, $category: String, $subCategory: String, $family: String, $cityId: Int!, $orderProfileType: OrderProfileType!, $priceVersion: Int, $finishId: String, $paymentStage: Int, $projectCreationDate: String, $isElemental: Boolean, $elementalType: String, $orderBookingDate: String) {
  getColour(materialId: $materialId, cityId: $cityId, materialType: $materialType, category: $category, subCategory: $subCategory, family: $family, orderProfileType: $orderProfileType, priceVersion: $priceVersion, finishId: $finishId, paymentStage: $paymentStage, projectCreationDate: $projectCreationDate, isElemental: $isElemental, elementalType: $elementalType, orderBookingDate: $orderBookingDate) {
    colourId
    colourName
    colourImage
    assetMaterialId
    __typename
  }
}
